import { prisma } from "@recipesage/prisma";
import { createTRPCProxyClient, httpLink } from "@trpc/client";
import type { AppRouter } from "./index";
import { faker } from "@faker-js/faker";
import { customTrpcTransformer } from "@recipesage/util/shared";

export async function trpcSetup() {
  const user = await createUser();
  const user2 = await createUser();
  const session = await createSession(user.id);
  const session2 = await createSession(user2.id);
  const trpc = await createTrpcClient(session.token as string);
  const trpc2 = await createTrpcClient(session2.token as string);
  return { user, session, trpc, user2, session2, trpc2 };
}

export async function tearDown(userId: string, userId2: string) {
  await prisma.user.delete({
    where: {
      id: userId,
    },
  });
  await prisma.user.delete({
    where: {
      id: userId2,
    },
  });
}

export async function createTrpcClient(token: string) {
  return createTRPCProxyClient<AppRouter>({
    links: [
      httpLink({
        url: "http://localhost:3000/trpc",
        headers: () => {
          return {
            Authorization: token ? `Bearer ${token}` : undefined,
          };
        },
      }),
    ],
    transformer: customTrpcTransformer,
  });
}

export async function createUser() {
  return prisma.user.create({
    data: {
      name: faker.person.fullName(),
      email: faker.internet.email(),
      passwordHash:
        "",
      passwordSalt:
        "dM4YXu5N5XY4c0LXnf30vtshh7dgsBYZ/5pZockgcJofPkWhMOplVAoWKhyqODZhO3mSUBqMqo3kXC2+7fOMt1NFB0Q1iRcJ4zaqAqdTenyjXu7rJ8WpgR1qnTcnpP8g/frQ+sk8Kcv49OC84R3v+FD8RrGm0rz8dDt7m7c/+Rw=",
      passwordVersion: 2,
    },
  });
}

export const createSession = (userId: string) => {
  const today = new Date();
  const tomorrow = new Date();
  tomorrow.setDate(today.getDate() + 1);

  return prisma.session.create({
    data: {
      userId,
      token: faker.string.alphanumeric(40),
      type: "user",
      expires: tomorrow,
    },
  });
};
